import React from 'react';
import pokemonList from './pokemon'; // ./ means check the current directory for this file
import './App.css';

// every component gets props
// props is an object containing all of the items that are passed into the component
// this is a component
function Pokemon(props) { 
  // this is equivalent to
  // const pokemon = props.pokemon;
  const { pokemon } = props;

  let caughtText = "Not caught";
  if (pokemon.caught) {
    caughtText = "Caught";
  }

  return <div className="pokemonRow">{pokemon.name}, {pokemon.type}, {caughtText}</div>
}

function App() {
  return (
    <div className="App">
      <h1>Pokedex</h1>
      {pokemonList.map(pokemon => {
        return <Pokemon pokemon={pokemon} />
      })}
    </div>
  );
}

export default App;
